         "generation_config": {
             "model": "gpt-4o-mini",
             "temperature": 0.7
         },
         "local_search_limits": {
             "__Entity__": 20,
             "__Relationship__": 20,
             "__Community__": 20
         },
         "max_community_description_length": 65536,
         "max_llm_queries_for_global_search": 250
       }
     }'

